mapper.select:
SELECT 
FROM finance_document_mapping
WHERE deleted = 0

    AND tenant_id = #{tenantId}


    AND shop_id = #{shopId}


    AND document_type = #{documentType}


    AND mapping_status = #{mappingStatus}


    AND document_no LIKE CONCAT('%', #{documentNo}, '%')


    AND order_no LIKE CONCAT('%', #{orderNo}, '%')


    AND create_time >= #{startTime}


    AND create_time <= #{endTime}

ORDER BY create_time DESC

SELECT 
    mapping_status,
    COUNT(*) as count,
    SUM(mapping_amount) as total_amount
FROM finance_document_mapping
WHERE deleted = 0

    AND tenant_id = #{tenantId}


    AND shop_id = #{shopId}


    AND document_type = #{documentType}

GROUP BY mapping_status

SELECT 
FROM finance_document_mapping
WHERE deleted = 0 AND mapping_status = 0

    AND tenant_id = #{tenantId}


    AND shop_id = #{shopId}


    AND document_type = #{documentType}

ORDER BY create_time ASC

mapper.update:
UPDATE finance_document_mapping SET mapping_status = #{mappingStatus}, mapping_time = NOW(), mapping_user = #{mappingUser}, updater = #{updater}, update_time = NOW() WHERE id IN
#{id}
AND deleted = 0
